'use strict'

module.exports=loopGenerator;
    
//generating new object for each loop request
function loopGenerator(data,array,customFunction,callbackFunction){
    new loop(data,array,customFunction,callbackFunction);
}

//function to do the async loop operation
function loop(data,array,customFunction,callbackFunction){
    if((typeof customFunction === "function")&&(typeof callbackFunction === "function")){

        array.forEach(function(keys,i){
            customFunction(data,keys);
            if((i+1)===array.length){
                callbackFunction(data)
            }
        })
        
        
    }
    else{
        console.log("Last two parameters should be of type function.")
    }
}

